 */
contract L1StandardERC20Factory {
    /**********
     * Events *
     **********/

    event ERC20Created(string indexed _symbol, address indexed _address);

    /********************
     * Public Functions *
     ********************/

    /**
     * Deploys the Oasys Standard ERC20.
